mapper.insert:
INSERT INTO tbl_common_code_sub(
        main_common_key, sub_common_name, note
)
VALUES (
        #{mainCommonKey}, #{subCommonName}, #{note}
)

mapper.select:
SELECT IF(COUNT(*) = 0, true, false) FROM tbl_common_code_sub sub INNER JOIN tbl_common_code_main main ON sub.main_common_key = main.main_common_key WHERE sub.sub_common_key IN
#{subCommonKey}
AND ( sub.is_deleted != 0 OR sub.is_immutable != 0 OR main.is_immutable != 0 )

SELECT

COUNT(*)
FROM tbl_common_code_sub
WHERE is_deleted = 0
AND sub_common_name = #{productType}
AND main_common_key = #{excelProductTypeKey}

SELECT

sub_common_key
FROM tbl_common_code_sub
WHERE is_deleted = 0
AND sub_common_name = #{productType}
AND main_common_key = #{excelProductTypeKey}

SELECT

COUNT(*)
FROM tbl_common_code_sub
WHERE is_deleted = 0
AND sub_common_name = #{unitName}
AND main_common_key = #{excelUnitKey}

SELECT

sub_common_key
FROM tbl_common_code_sub
WHERE is_deleted = 0
AND sub_common_name = #{unitName}
AND main_common_key = #{excelUnitKey}
