mapper.select:
SELECT IF(COUNT(*) > 0, true, false) AS result
FROM tbl_product_company
WHERE is_deleted = 0
  AND product_key = #{productKey}
  AND company_key = #{companyKey}

mapper.insert:
INSERT INTO tbl_product_company (
     product_key, company_key
)
VALUES (
    #{productKey}, #{companyKey}
)
