mapper.insert:
INSERT INTO tbl_product (
    product_type_key, unit_key, process_type_key, drawing_key,
    product_code, product_name, standard_price, asset_type_flag, safety_quantity
)
VALUES (
        #{productTypeKey}, #{unitKey}, #{processTypeKey}, #{drawingKey},
        #{productCode}, #{productName}, #{standardPrice}, #{assetTypeFlag}, #{safetyQuantity}
       )

mapper.select:
SELECT pro.product_key, pro.product_type_key, sub.sub_common_name AS product_type_name, pro.unit_key, sub2.sub_common_name AS unit_name, pro.process_type_key, sub3.sub_common_name AS process_type_name, pro.drawing_key, sub4.sub_common_name AS drawing_name, pro.product_code, pro.product_name, pro.standard_price, pro.asset_type_flag, CASE WHEN pro.asset_type_flag = 'M' THEN CAST(pro.safety_quantity AS CHAR) WHEN pro.asset_type_flag = 'F' THEN CAST(ROUND(pro.safety_quantity) AS CHAR) END AS safety_quantity, ( SELECT SUM(inven.quantity) FROM tbl_inventory inven WHERE inven.product_key = pro.product_key ) AS total_quantity FROM tbl_product pro LEFT JOIN tbl_common_code_sub sub ON pro.product_type_key = sub.sub_common_key LEFT JOIN tbl_common_code_sub sub2 ON pro.unit_key = sub2.sub_common_key LEFT JOIN tbl_common_code_sub sub3 ON pro.process_type_key = sub3.sub_common_key LEFT JOIN tbl_common_code_sub sub4 ON pro.drawing_key = sub4.sub_common_key WHERE pro.is_deleted = 0
AND pro.product_code LIKE CONCAT('%',#{productCode},'%')
AND pro.product_name LIKE CONCAT('%',#{productName},'%')
AND pro.product_type_key = #{productTypeKey}
AND pro.process_type_key = #{processTypeKey}
AND pro.asset_type_flag = #{assetTypeFlag}
AND com.company_type_flag = #{companyTypeFlag}
ORDER BY pro.product_key DESC
${sort}
LIMIT ${record} OFFSET ${offset}
