mapper.insert:
INSERT INTO tbl_user (
    user_code, password, user_name, department_key, position_key,
    permission_type_flag, phone_number

        ,user_image_key


        ,user_signature_key

)
VALUES (
    #{userCode}, #{password}, #{userName}, #{departmentKey}, #{positionKey},
    #{permissionTypeFlag}, #{phoneNumber}

        , #{userImageKey}


        , #{userSignatureKey}

)

mapper.select:
SELECT password
FROM tbl_user
WHERE is_deleted = 0
  AND user_key = #{userKey}
  AND user_code = #{userCode}

mapper.update:
UPDATE tbl_user
SET token_date = #{tokenDate}
WHERE is_deleted = 0
  AND user_key = #{userKey}
  AND user_code = #{userCode}

mapper.select:
SELECT user_code, token_date
FROM tbl_user
WHERE is_deleted = 0
  AND user_key = #{userKey}
  AND token_date = #{tokenDate}

SELECT user.user_key, user.user_code, user.user_name, sub.sub_common_key as department_key, sub.sub_common_name as department_name, sub2.sub_common_key as position_key, sub2.sub_common_name as position_name, user.permission_type_flag, user.phone_number FROM tbl_user user LEFT JOIN tbl_common_code_sub sub ON user.department_key = sub.sub_common_key LEFT JOIN tbl_common_code_sub sub2 ON user.position_key = sub2.sub_common_key WHERE user.is_deleted = 0 AND user.permission_type_flag != 'R'
AND user.user_name LIKE CONCAT('%',#{userName},'%')
AND user.user_code LIKE CONCAT('%',#{userCode},'%')
AND user.department_key = #{departmentKey}
AND user.position_key = #{positionKey}
AND user.permission_type_flag = #{permissionTypeFlag}
AND user.permission_type_flag = 'U'
AND user.permission_type_flag IN ('U', 'A')
AND user.permission_type_flag IN ('U', 'A', 'R')
ORDER BY user.reg_date_time DESC
${sort}
LIMIT ${record} OFFSET ${offset}

mapper.update:
UPDATE tbl_user
SET token_date = NULL
WHERE user_code = #{userCode}

mapper.select:
SELECT COUNT(user.user_key) FROM tbl_user user LEFT JOIN tbl_common_code_sub sub ON user.department_key = sub.sub_common_key LEFT JOIN tbl_common_code_sub sub2 ON user.position_key = sub2.sub_common_key LEFT JOIN tbl_images im ON user.user_image_key = im.image_key AND im.is_deleted = 0 LEFT JOIN tbl_images sim on user.user_signature_key = sim.image_key AND sim.is_deleted = 0 WHERE user.is_deleted = 0 AND user.permission_type_flag != 'R'
AND user.user_name LIKE CONCAT('%',#{userName},'%')
AND user.user_code LIKE CONCAT('%',#{userCode},'%')
AND user.department_key = #{departmentKey}
AND user.position_key = #{positionKey}
AND user.permission_type_flag = #{permissionTypeFlag}
AND user.permission_type_flag = 'U'
AND user.permission_type_flag IN ('U', 'A')
AND user.permission_type_flag IN ('U', 'A', 'R')
